/*!/wp-content/plugins/designthemes-doctor-addon/js/doctors.js*/
jQuery("document").ready(function($){$('div.dt-sc-doctors-sorting > a').on('click',function(e){$("div.dt-sc-doctors-sorting > a").removeClass("active-sort");$(this).addClass("active-sort");doctor_filter();e.preventDefault()});$("select[name=department-filter]").change(function(){doctor_filter()});if($("ul.dt-sc-team-carousel").length){$("ul.dt-sc-team-carousel").each(function(){var $prev=$(this).parents(".dt-sc-team-carousel-wrapper").find(".dt-sc-team-pager-prev");var $next=$(this).parents(".dt-sc-team-carousel-wrapper").find(".dt-sc-team-pager-next");$(this).carouFredSel({responsive:!0,auto:!1,width:'100%',height:'variable',prev:$prev,next:$next,scroll:{fx:'crossfade',duration:1000},items:1})})}
function doctor_filter(){var $data={'action':'dt_sc_filter_doctors','data':{'title':$('div.dt-sc-doctors-sorting > a.active-sort').html(),'tax':$("select[name=department-filter]").val(),'column':$('div.dt-sc-doctors-container').data('column')}};jQuery.ajax({url:dttheme_urls.ajaxurl,data:$data,beforeSend:function(){$("div.dt-sc-doctors-container").html('<div class="dt-sc-loading"></div>')},success:function(response){$("div.dt-sc-doctors-container").html(response).fadeIn()}})}})
;